import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { useCallback } from 'react';
import { StyleSheet, Text, View, TextInput, Image } from 'react-native';
import { useFonts } from 'expo-font';
import * as SplashScreen from 'expo-splash-screen';

const Login = () => {
    /*const [fontsLoaded] = useFonts({
      'Comic': require('./assets/fonts/ComicNeue-Regular.ttf')
    })*/

    /*let [fontsLoaded] = useFonts({
      'Comic': require('./assets/fonts/ComicNeue-Regular.ttf'),
    });
    if (!fontsLoaded) {
        return <AppLoading/>;
    }*/

    const [fontsLoaded] = useFonts({
        'Comic': require('../assets/fonts/ComicNeue-Regular.ttf'),
    });

    const onLayoutRootView = useCallback(async () => {
        if (fontsLoaded) {
            await SplashScreen.hideAsync();
        }
    }, [fontsLoaded]);

    if (!fontsLoaded) {
        return null;
    }

    return (
        <View style={styles.container} onLayout={onLayoutRootView}>
            <StatusBar style="auto" />
            <Image source={require('../assets/images/firstbg.png')} style={styles.backgroundImage} />
            <View style={[styles.login_container, styles.shadowProp]}>
                <View style={styles.textView}>
                    <Text style={styles.loginHeader}>Giriş Yap</Text>
                </View>

                <TextInput
                    style={styles.texti}
                    placeholder="Email"
                    keyboardType="text"
                />

                <TextInput
                    style={styles.texti}
                    placeholder="Password"
                    keyboardType="text"
                />
            </View>
        </View>
    );
}

export default Login;

const styles = StyleSheet.create({
    container: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',

    },

    backgroundImage: {
        flex: 1,
        resizeMode: 'cover',
    },

    login_container: {
        backgroundColor: '#FFF',
        width: '100%',
        height: '50%',
        position: 'absolute',
        bottom: 0,
        alignItems: 'center',
        borderTopLeftRadius: 50,
        borderTopRightRadius: 50,      
    },

    shadowProp: {
        shadowColor: '#000',
        shadowOffset: {width: -2, height: 4},
        shadowOpacity: 1,
        shadowRadius: 3,
        elevation: 13,
      },

    texti: {
        fontFamily: 'Comic',
        width: 350,
        height: 50,
        marginHorizontal: 10,
        marginVertical: 10,
        backgroundColor: "#FFFFFF",
        borderRadius: 15,
        borderWidth: 2,
        borderColor: "#DDD",
        paddingLeft: 20,
        paddingTop: 10,
        paddingBottom: 10,
    },

    textView: {
        padding: 15,   
    },

    loginHeader: {
        fontFamily: 'Comic',
        fontSize: 40,   
    },
});
